const btnsSizes=document.querySelectorAll("ul.dropdown-menu-taille > li > button"),valuesSizes=["8px","12px","16px","18px","20px","22px"],lineHeights=["50px","75px","150px","150px","150px","150px"];function changeSize(e,t,n=0){const i=document.querySelector(":not(.container)");t.addEventListener("click",()=>{i.style.fontSize=e,localStorage.setItem("size",JSON.stringify(e))})}document.addEventListener("DOMContentLoaded",async()=>{for(let e=0;e<btnsSizes.length;e++)changeSize(valuesSizes[e],btnsSizes[e],lineHeights[e])});